import React, { useState, useEffect  } from "react";
import NavbarAdmin from "../components/NavbarAdmin";
import Footer from "../components/Footer";
import axios from 'axios';


export const AvisScreen = () => {

  // pour le menu toggle 
  const [isMenuOpen, setMenuOpen] = useState(false);

  const handleMenuOpen = () => {
    setMenuOpen(!isMenuOpen);
  };


  // pour recuperer les avis
  const [avis, setAvis] = useState([]);


    const fetchAvis = async () => {
      try {
        const response = await axios.get('http://localhost:5000/avis/GetAvis');
        setAvis(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    useEffect(() => {
      fetchAvis();
    }, []);


  // ------------------pour la pagination------------------------------
  const [currentPage, setCurrentPage] = useState(1);
  const [avisPerPage] = useState(10);
  // Calculer le nombre total de pages
  const totalPages = Math.ceil(avis.length / avisPerPage);

  // Créer un tableau pour les numéros de page
  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
